SELECT books_for_pc.Name, books_for_pc.Price, Publishers.publisher_surname FROM books_for_pc,Publishers WHERE books_for_pc.Publisher=Publishers.publisher_id;
SELECT books_for_pc.Name,Categories.category_name FROM books_for_pc INNER JOIN Categories ON books_for_pc.Category=Categories.category_id;
SELECT books_for_pc.Name ,books_for_pc.Price ,Publishers.publisher_surname,Formats.format_name FROM books_for_pc INNER JOIN Publishers INNER JOIN Formats ON books_for_pc.Publisher=Publishers.publisher_id AND books_for_pc.Format=Formats.format_id;
SELECT books_for_pc.Theme,books_for_pc.Category,books_for_pc.Name,Publishers.publisher_surname FROM books_for_pc INNER JOIN Topics INNER JOIN Publishers ON books_for_pc.Publisher=Publishers.publisher_id AND books_for_pc.Theme=Topics.topic_id;
SELECT books_for_pc.Name,YEAR(books_for_pc.Date_book),Publishers.publisher_surname FROM books_for_pc INNER JOIN Publishers ON Publishers.publisher_id=books_for_pc.Publisher WHERE Publishers.publisher_surname LIKE"BHV%" AND YEAR(books_for_pc.Date_book)>=2000;
SELECT Categories.category_name,SUM(books_for_pc.Pages_count) FROM books_for_pc INNER JOIN Categories ON books_for_pc.Category=Categories.category_id GROUP BY Categories.category_id ORDER BY SUM(books_for_pc.Pages_count) DESC;
SELECT AVG(books_for_pc.Price),Topics.topic_name,Categories.category_name FROM books_for_pc INNER JOIN Topics INNER JOIN Categories ON books_for_pc.Theme=Topics.topic_id AND books_for_pc.Category=Categories.category_id WHERE Topics.topic_name LIKE '%Використання ПК%' AND Categories.category_name LIKE '%Linux%' GROUP BY Topics.topic_name, Categories.category_name;
SELECT books_for_pc.N,books_for_pc.Code,books_for_pc.Is_new,books_for_pc.Price,Publishers.publisher_surname,books_for_pc.Pages_count,Formats.format_name,books_for_pc.Date_book,books_for_pc.Tirage,Topics.topic_name,Categories.category_name FROM books_for_pc,Publishers,Categories,Formats,Topics WHERE books_for_pc.Format=Formats.format_id AND books_for_pc.Publisher=Publishers.publisher_id AND books_for_pc.Theme=Topics.topic_id AND books_for_pc.Category=Categories.category_id;
SELECT books_for_pc.N,books_for_pc.Code,books_for_pc.Is_new,books_for_pc.Price,Publishers.publisher_surname,books_for_pc.Pages_count,Formats.format_name,books_for_pc.Date_book,books_for_pc.Tirage,Topics.topic_name,Categories.category_name FROM books_for_pc INNER JOIN Publishers INNER JOIN Categories INNER JOIN Formats INNER JOIN Topics ON books_for_pc.Format=Formats.format_id AND books_for_pc.Publisher=Publishers.publisher_id AND books_for_pc.Theme=Topics.topic_id AND books_for_pc.Category=Categories.category_id;
SELECT books_for_pc.N,books_for_pc.Code,books_for_pc.Is_new,books_for_pc.Price,Publishers.publisher_surname,books_for_pc.Pages_count,Formats.format_name,books_for_pc.Date_book,books_for_pc.Tirage,Topics.topic_name,Categories.category_name FROM books_for_pc LEFT JOIN Formats ON books_for_pc.Format=Formats.format_id LEFT JOIN Publishers ON books_for_pc.Publisher=Publishers.publisher_id LEFT JOIN Categories ON books_for_pc.Category=Categories.category_id  LEFT JOIN  Topics ON books_for_pc.Theme=Topics.topic_id;
SELECT DISTINCT book1.Name,book2.Name FROM books_for_pc book1 JOIN books_for_pc book2 ON book1.Pages_count=book2.Pages_count AND book1.N!=book2.N;
SELECT DISTINCT book1.Name,book2.Name,book3.Name FROM books_for_pc book1 JOIN books_for_pc book2 ON book1.Price=book2.Price AND book1.N!=book2.N JOIN books_for_pc book3 ON book2.Price=book3.Price AND book2.N!=book3.N;
SELECT * from books_for_pc WHERE Category = (SELECT category_id FROM Categories WHERE Categories.category_name = 'C&C++');
SELECT * from books_for_pc WHERE Publisher = (SELECT publisher_id FROM Publishers WHERE Publishers.publisher_surname = '%BHV%') AND YEAR(Date_book)>2000;
SELECT * FROM Publishers Where( SELECT MIN(Pages_count) FROM books_for_pc WHERE books_for_pc.Publisher=Publishers.publisher_id)>400;
SELECT * FROM Categories WHERE(SELECT COUNT(*) FROM books_for_pc WHERE books_for_pc.Category=Categories.category_id)>3;
SELECT * FROM books_for_pc WHERE EXISTS (SELECT * FROM Publishers WHERE Publishers.publisher_surname = 'Видавнича група BHV' AND Publishers.publisher_id= books_for_pc.Publisher);
SELECT * FROM books_for_pc WHERE NOT EXISTS (SELECT * FROM Publishers WHERE Publishers.publisher_surname = 'Видавнича група BHV' AND Publishers.publisher_id= books_for_pc.Publisher);
((SELECT * FROM Topics) UNION (SELECT * FROM Categories)) ORDER BY topic_name;
SELECT DISTINCT Name FROM ((SELECT REGEXP_SUBSTR(TRIM(books_for_pc.Name),'^[^\\s]+') AS Name FROM books_for_pc) UNION ALL(SELECT REGEXP_SUBSTR(TRIM(Categories.category_name),'^[^\\s]+') FROM Categories)) books_for_pc,Categories ORDER BY name DESC;